/**
  * @brief  The application entry point.
  *
  * @retval None
  */
int main(void)
{
  /* USER CODE BEGIN 1 */
int last_state_was_down = 0;
TS_StateTypeDef TsState;
  /* USER CODE END 1 */

  /* MCU Configuration----------------------------------------------------------*/

  /* Reset of all peripherals, Initializes the Flash interface and the Systick. */
  HAL_Init();

  /* USER CODE BEGIN Init */

  /* USER CODE END Init */

  /* Configure the system clock */
  SystemClock_Config();

  /* USER CODE BEGIN SysInit */

  /* USER CODE END SysInit */

  /* Initialize all configured peripherals */
  MX_GPIO_Init();
  MX_DMA2D_Init();
  MX_FMC_Init();
  MX_LTDC_Init();
  MX_I2C3_Init();
  MX_SPI5_Init();
  /* USER CODE BEGIN 2 */
BSP_LCD_Init();
BSP_LCD_LayerDefaultInit(LCD_BACKGROUND_LAYER,LCD_FRAME_BUFFER);
BSP_LCD_LayerDefaultInit(LCD_FOREGROUND_LAYER,LCD_FRAME_BUFFER);
BSP_LCD_DisplayOn();
BSP_LCD_SelectLayer(LCD_FOREGROUND_LAYER);
BSP_TS_Init(240, 320);
unsigned long last_display_update = HAL_GetTick();
  /* USER CODE END 2 */

  /* Infinite loop */
  /* USER CODE BEGIN WHILE */
  while (1)
  {

  /* USER CODE END WHILE */

  /* USER CODE BEGIN 3 */
		updateReadings();
		// Handle periodic drawing (once every 16ms = 60FPS)
		int nowMS = HAL_GetTick();
		if (nowMS-last_display_update > 16) {
			last_display_update += 16;
			// Fix very slow cycles ahead
			if (nowMS-last_display_update > 16) {
				last_display_update = nowMS;
			}
			displayState();
		}
		checkAlarmState();
		
		BSP_TS_GetState(&TsState);
		if(TsState.TouchDetected)
		{
			if (last_state_was_down) {
				last_state_was_down = 1;
				processEvent(TOUCH_START, TsState.X, TsState.Y);
			} else {
				processEvent(TOUCH_MOVE, TsState.X, TsState.Y);
			}
		} else {
			if (last_state_was_down) {
				last_state_was_down = 0;
				processEvent(TOUCH_END, TsState.X, TsState.Y);
			}
		}
		HAL_Delay(10);
  }
  /* USER CODE END 3 */

}

