typedef enum {
	TOUCH_START=1,
	TOUCH_MOVE=2,
	TOUCH_END=3
} EVENT_TYPE;

void processEvent(EVENT_TYPE type, int x, int y) {
	if (type == TOUCH_START) {
		if (state == DEBUG) {
			if (x > 111-5 && x < 111+115+5 && y > 268-5 && y < 265+40+5) {
				state = HOME;
				redraw_info = 0;
			}
		} else if (state == HOME) {
			state = DEBUG;
			redraw_info = 0;
		}
	}
	last_x = x;
	last_y = y;
	last_state = type;
	//last_state = (type == TOUCH_START || type == TOUCH_MOVE);
	if (redraw_info == 2) {
		redraw_info = 1;
	}
}


void updateReadings() {
	static int x = 0;
	static unsigned char b1, b2;
	static unsigned long last_request = 0;
	unsigned long difTime;
	int ack;
	double raw;
	switch (x) {
		case 0: // Start communication
			start_i2c_SHT(); break;
		case 1: // Request humidity
			envia_byte_i2c(0x05);
			ack = read_ack_i2c();
			if (ack == 1) {
				errNumber = 1;
				x = -1;
			} else {
				last_request = HAL_GetTick();
				set_SDA_as_input();
			}
			break;
		case 2: // Wait for response of humidity
			difTime = HAL_GetTick()-last_request;
			if (difTime > 140) {
				errNumber = 2; // Timeout
				x = -1;
			} else if (difTime > 40) {
				if (difTime < 70) {
					HAL_Delay(2);
				}
				if (R(G, SDA) == 0) {
					b1 = 0;
					b2 = 0;
				} else {
					x--;
				}
			} else if (difTime > 0) {
				HAL_Delay(2);
				x--;
			} else {
				 x = -1; errNumber = 3; // Internally too fast
			}
			break;
		case 3:
			b1 = le_byte_i2c();
			break;
		case 4:
			b2 = le_byte_i2c();
			break;
		case 5:
			raw = ((0xf & b1) << 8) | b2;
			if (humid.raw != raw) {
				humid.raw = raw;
				humid.value = (double) -2.0468F+0.0367F*raw-1.5955E-6*raw*raw;
				humid.updated = 1;
			}
			break;
		case 6:
			break;
		case 7:
			start_i2c_SHT();
			break;
		case 8: // Request temp
			envia_byte_i2c(0x03);
			ack = read_ack_i2c();
			if (ack == 1) {
				x = -1; errNumber = 4;
			} else {
				last_request = HAL_GetTick();
				set_SDA_as_input();
			}
			break;
		case 9: // Wait for response of temp
			difTime = HAL_GetTick()-last_request;
			if (difTime > 340) {
				errNumber = 5; // Timeout
				x = -1;
			} else if (difTime > 50) {
				if (difTime < 70) {
					HAL_Delay(2);
				}
				if (R(G, SDA) == 0) {
					b1 = 0;
					b2 = 0;
				} else {
					x--;
				}
			} else if (difTime > 0) {
				HAL_Delay(2);
				x--;
			} else {
				 x = -1; errNumber = 6; // Internally too fast
			}
			break;
		case 10:
			b1 = le_byte_i2c();
			break;
		case 11:
			b2 = le_byte_i2c();
			break;
		case 12:
			raw = ((0xf & b1) << 8) | b2;
			if (temp.raw != raw) {
				temp.raw = raw;
				temp.value = (double) 0.024F*((double)raw)-37.6F;
				temp.updated = 1;
			}
			break;
		case 13:
			x = 0;
			start_i2c_SHT();
			break;
	}
	if (x >= 0 && x <= 20) { 
		x = x>20?0:x+1;
	}
}
